import React, { useEffect } from "react";
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import ImagePopup from "./ImagePopup";
import api from "../utils/Api";
import CurrentUserContext from "../contexts/CurrentUserContext";
import EditProfilePopup from "./EditProfilePopup";
import EditAvatarPopup from "./EditAvatarPopup";
import AddPlacePopup from "./AddPlacePopup";
import Register from "./Register";
import Login from "./Login";
import ProtectedRoute from "./ProtectedRoute";
import { Link, useNavigate } from "react-router-dom";
import { Route, Routes } from "react-router-dom";
import * as auth from "../utils/auth";
import InfoTooltip from "./InfoTooltip";

function App() {
    const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
    const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
    const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
    const [isRegisterPopupOpen, setIsRegisterPopupOpen] = React.useState(false);
    const [selectedCard, setSelectedCard] = React.useState({ name: "", link: "" });
    const [currentUser, setCurrentUser] = React.useState({});
    const [cards, setCards] = React.useState([]);
    const [loggedIn, setLoggedIn] = React.useState(false);
    const [email, setEmail] = React.useState("");
    const [password, setPassword] = React.useState("");
    const [registerInfo, setRegisterInfo] = React.useState("");
    const [registerStatus, setRegisterStatus] = React.useState(false);


    const navigate = useNavigate();

    React.useEffect(() => {
        api.getProfile()
            .then((userData) => {
                setCurrentUser(userData);
            })
            .catch((err) => {
                console.log("userDataError", err);
            });
    }, []);

    React.useEffect(() => {
        api.getInitialCards()
            .then((cardsData) => {
                setCards(cardsData);
            })
            .catch((err) => {
                console.log("initialCards", err);
            });
    }, []);

    const handleCardDelete = (id) => {
        api.delImage(id)
            .then(() => {
                setCards((cards) => cards.filter((c) => c._id !== id));
            })
            .catch((err) => {
                console.log("deletingCard", err);
            });
    };

    function handleCardLike(card) {
        const isLiked = card.likes.some((i) => i._id === currentUser._id);

        api.changeLikeCardStatus(card._id, isLiked)
            .then((newCard) => {
                setCards((state) => state.map((c) => (c._id === card._id ? newCard : c)));
            })
            .catch((err) => {
                console.log("likeCard", err);
            });
    }

    function handleUpdateUser(userData) {
        api.editProfile(userData.name, userData.about)
            .then((userData) => {
                setCurrentUser(userData);
                closeAllPopups();
            })
            .catch((err) => {
                console.log("editProfile", err);
            });
    }

    function handleUpdateAvatar(avatarData) {
        api.changeAvatar(avatarData.avatar).then((avatarData) => {
            setCurrentUser(avatarData);
            closeAllPopups();
        });
    }

    function handleAddPlaceSubmit(newCard) {
        api.addImage(newCard.name, newCard.link, newCard.likes).then((newCard) => {
            setCards([newCard, ...cards]);
            closeAllPopups();
        });
    }

    function closeAllPopups() {
        setIsEditProfilePopupOpen(false);
        setIsAddPlacePopupOpen(false);
        setIsEditAvatarPopupOpen(false);
        setIsRegisterPopupOpen(false);
        setSelectedCard({ name: "", link: "" });
    }

    function handleLogin() {
        setLoggedIn(true);
    }

    React.useEffect(() => {
        tokenCheck();
    }, []);

    function tokenCheck() {
        const token = localStorage.getItem("token");

        if (token) {
            auth.getContent(token)
                .then((res) => {
                    if (res) {
                        const userData = {
                            email: res.email,
                        };
                        setLoggedIn(true);
                        setCurrentUser(userData); // добавляем в стейт данные почты
                        navigate("/", { replace: true });
                    }
                })
                .catch((err) => {
                    console.log("TokenCheckError", err);
                });
        }
    }

    function handleRegister() {
        setIsRegisterPopupOpen(true);
    }

    function handleEmailChange(evt) {
        setEmail(evt.target.value);
    }

    function handlePasswordChange(evt) {
        setPassword(evt.target.value);
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        auth.register(email, password)
            .then((res) => {
                if (res) {
                    setRegisterInfo("Вы успешно зарегистрировались!");
                    setRegisterStatus(true);
                    handleRegister();
                    navigate("/sign-in", { replace: true });
                } else {
                    setRegisterInfo("Что-то пошло не так! Попробуйте ещё раз.");
                    handleRegister();
                }
            })
            .catch((err) => {
                console.log("Error", err);
            });
    };

    return (
        <div>
            <CurrentUserContext.Provider value={currentUser}>
                {/* <Header /> */}
                <Routes>
                    <Route
                        path="/"
                        element={
                            <ProtectedRoute
                                element={Main}
                                loggedIn={loggedIn}
                                onEditProfile={() => setIsEditProfilePopupOpen(true)}
                                onAddPlace={() => setIsAddPlacePopupOpen(true)}
                                onEditAvatar={() => setIsEditAvatarPopupOpen(true)}
                                onCardClick={setSelectedCard}
                                cards={cards}
                                onCardDelete={handleCardDelete}
                                onCardLike={handleCardLike}
                            />
                        }
                    />
                    <Route
                        path="/sign-up"
                        element={
                            <Register
                                handleEmailChange={handleEmailChange}
                                handlePasswordChange={handlePasswordChange}
                                handleSubmit={handleSubmit}
                            />
                        }
                    />
                    <Route path="/sign-in" element={<Login handleLogin={handleLogin} />} />
                </Routes>

                <ImagePopup card={selectedCard} onClose={closeAllPopups} />
                <EditProfilePopup isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} onUpdateUser={handleUpdateUser} />
                <EditAvatarPopup isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups} onUpdateAvatar={handleUpdateAvatar} />
                <AddPlacePopup isOpen={isAddPlacePopupOpen} onClose={closeAllPopups} onAddPlace={handleAddPlaceSubmit} />
                <InfoTooltip isOpen={isRegisterPopupOpen} onClose={closeAllPopups} registerInfo={registerInfo} registerStatus={registerStatus} />
                {loggedIn && <Footer />}
            </CurrentUserContext.Provider>
        </div>
    );
}

export default App;
