import React from "react";
import api from "./Api";

function Main({ onEditProfile, onAddPlace, onEditAvatar }) {
    const [userName, setUserName] = React.useState("");
    const [userDescription, setUserDescription] = React.useState("");
    const [userAvatar, setUserAvatar] = React.useState("");
    const [cards, setCards] = React.useState([]);

    React.useEffect(() => {
        api.getProfile()
            .then((userData) => {
                setUserName(userData.name);
                setUserDescription(userData.about);
                setUserAvatar(userData.avatar);
            })
            .catch((err) => {
                console.log("userData", err);
            });
    }, [userName, userDescription, userAvatar]);

    React.useEffect(() => {
        api.getInitialCards().then((cardsData) => {
            console.log(cardsData);
            setCards(
                cardsData.map((item) => {
                    return (
                        <template>
                            <div className="element" id={item._id}>
                                <img className="element__image" />
                                <button className="element__del-button" type="button"></button>
                                <div className="element__box">
                                    <h2 className="element__title">{item.name}</h2>
                                    <div className="element__like-box">
                                        <button className="element__like-button" type="button"></button>
                                        <span className="element__like-score"></span>
                                    </div>
                                </div>
                            </div>
                        </template>
                    );
                })
            );
        });
    }, [cards]);

    return (
        <main>
            <section className="profile">
                <div className="profile__box">
                    <div className="profile__image-box">
                        <img className="profile__image" src={userAvatar} alt="Фото профиля" />
                        <div className="profile__image-overlay">
                            <span className="profile__image-edit" onClick={onEditAvatar}></span>
                        </div>
                    </div>
                    <div className="profile__info">
                        <div className="profile__title-container">
                            <h1 className="profile__title" name="name">
                                {userName}
                            </h1>
                            <button className="profile__edit-button" type="button" onClick={onEditProfile}></button>
                        </div>
                        <h2 className="profile__subtitle">{userDescription}</h2>
                    </div>
                </div>
                <button className="profile__add-button" onClick={onAddPlace}></button>
            </section>
            <section className="elements">
                <div className="elements__list">{cards}</div>
            </section>
        </main>
    );
}

export default Main;
