import React, { useState } from "react";
import {Link, useNavigate, Navigate} from 'react-router-dom';
import * as auth from '../utils/auth';

function Register() {
    const [formValue, setFormValue] = useState({
        email: "",
        password: "",
    });

    //const navigate = useNavigate();

    const handleChange = (e) => {
        const {name, value} = e.target;

        setFormValue({
          ...formValue,
          [name]: value
        });
      }
      const handleSubmit = (e) => {
        e.preventDefault();
        const { email, password } = formValue;
        auth.register(email, password).then((res) => {
            // navigate("/sign-in", {replace: true})
            <Navigate to="/sign-in" replace/>
        })
      }

    return (
        <div className="register">
            <h2 className="register__title">Регистрация</h2>
            <form className="register__form" onSubmit={handleSubmit}>
                <input placeholder="Email" onChange={handleChange} />
                <input placeholder="Пароль" onChange={handleChange} />
                <button className="register__form-button" onSubmit={handleSubmit}>Зарегистрироваться</button>
            </form>
            <div className="register__sign-in">
                <p className="">Уже зарегистрированы?</p>
                <Link to="/sign-in" className="register__login-link">Войти</Link>
            </div>
        </div>
    );
}

export default Register;
